// ตัวแปรสำหรับเก็บค่าภาพที่ถูกเลือก
let currentFortuneIndex = null;

// จำนวนครั้งที่ผู้ใช้สามารถทำนายได้
let remainingGuesses = 3;

// อัพเดตจำนวนครั้งที่เหลือ
function updateRemainingCount() {
    document.getElementById('remaining-count').textContent = remainingGuesses;
}

// ฟังก์ชันสุ่มภาพทำนายดวง
function generateFortuneImage() {
    if (remainingGuesses > 0) {
        currentFortuneIndex = Math.floor(Math.random() * fortuneImages.length);
        const selectedImage = fortuneImages[currentFortuneIndex];

        // เปลี่ยนภาพใน <img> ที่มี id="fortune-image"
        document.getElementById('fortune-image').src = selectedImage;

        // เปิดใช้งานปุ่ม "ดูคำอธิบาย"
        document.getElementById('description-button').disabled = false;

        // รีเซ็ตข้อความคำอธิบาย
        document.getElementById('description-text').textContent = '';

        // ลดจำนวนครั้งที่เหลือ
        remainingGuesses--;
        updateRemainingCount();

        // หากไม่มีครั้งที่เหลือ ปิดการใช้งานปุ่ม
        if (remainingGuesses === 0) {
            document.getElementById('fortune-button').disabled = true;
        }
    }
}

// ฟังก์ชันแสดงคำอธิบายที่ตรงกับภาพ
function showDescription() {
    if (currentFortuneIndex !== null) {
        document.getElementById('description-text').textContent = descriptions[currentFortuneIndex];
    }
}

// อัพเดตจำนวนครั้งที่เหลือเริ่มต้น
updateRemainingCount();